import Header from '../Header';

import '../App.css';

import BotaoVoltar from '../componentes/BotaoVoltar';

//Utilizada para auxiliar no controle de outras funcoes da aplicacao
import React, { useState,useEffect } from 'react';

import axios from 'axios';

function Cadastro() {

    //cria novo estado para os campos da tela
    const [campos, setCampos] = useState({
        nome: '',
        idade: 0,
        cidade: ''
    });

    const [mensagem, setMensagem] = useState('');

    const [atualizacao, setAtualizacao] = useState(0);

    // Log do estado para debugging
  useEffect(() => {
    console.log('Estado atualizado:', campos);
  }, [campos]);


    // Log de atualização forçada para debugging
    useEffect(() => {
        console.log('Atualização forçada:', atualizacao);
      }, [atualizacao]);

    function handleInputChange(event) {
        campos[event.target.name] = event.target.value;
        setCampos(campos);
    }

     function handleFormSubmit(event){
        event.preventDefault();
        console.log(campos);
        axios.post('http://localhost:3001/api/usuarios', campos).then(response => {
            setMensagem('Formulário enviado com sucesso!');
        })

        // Mostrar mensagem de confirmação
        setMensagem('Formulário enviado com sucesso!');

        // Limpar os campos do formulário após o envio
        setCampos({
            nome: '',
            idade: '',
            cidade: ''
        });

    
// Forçar uma re-renderização adicional
setAtualizacao(prevAtualizacao => prevAtualizacao + 1);

        // Limpar mensagem após 3 segundos
        setTimeout(() => {
            setMensagem('');
        }, 3000);   
    }


    return (
        <div className="App">
            <Header title="Formulario de Cadastro" />

            <form onSubmit={handleFormSubmit}>
                <fieldset>
                    <legend>
                        <h2>Dados de Cadastro</h2>
                    </legend>

                    <div>
                        <label>Nome:
                            <input type="text" name="nome" id="nome"  onChange={handleInputChange}/>
                        </label>
                    </div>

                    <div>
                        <label>Idade:
                            <input type="number" name="idade" id="idade"  onChange={handleInputChange}/>
                        </label>
                    </div>

                    <div>
                        <label>Cidade:
                            <input type="text" name="cidade" id="cidade"  onChange={handleInputChange}/>
                        </label>
                    </div>


                    <input type="submit" value="Salvar" />
                </fieldset>
            </form>
            {mensagem && <p>{mensagem}</p>}
            <BotaoVoltar></BotaoVoltar>

        </div>
    )
}

export default Cadastro;
