import Header from '../Header';

import '../App.css';

import BotaoVoltar from '../componentes/BotaoVoltar';

//Utilizada para auxiliar no controle de outras funcoes da aplicacao
import { useState } from 'react';

import axios from 'axios';

function Cadastro() {

 //cria novo estado para os campos da tela
    const [campos, setCampos] = useState({
        nome: '',
        idade: 0,
        cidade: ''
    });
     
    function handleInputChange(event) {
        campos[event.target.name] = event.target.value;
        setCampos(campos);
    }

     function handleFormSubmit(event){
        event.preventDefault();
        console.log(campos);
        axios.post('http://localhost:3001/api/usuarios', campos).then(response => {
            alert(response.data + ' cadastros!');
        })
    }


    return (
        <div className="App">
            <Header title="Formulario de Cadastro" />

            <form onSubmit={handleFormSubmit}>
                <fieldset>
                    <legend>
                        <h2>Dados de Cadastro</h2>
                    </legend>

                    <div>
                        <label>Nome:
                            <input type="text" name="txtNome" id="txtNome" onChange={handleInputChange}/>
                        </label>
                    </div>

                    <div>
                        <label>Idade:
                            <input type="number" name="txtIdade" id="txtIdade" onChange={handleInputChange}/>
                        </label>
                    </div>

                    <div>
                        <label>Cidade:
                            <input type="text" name="txtCidade" id="txtCidade" onChange={handleInputChange}/>
                        </label>
                    </div>


                    <input type="submit" value="Salvar" />
                </fieldset>
            </form>

            <BotaoVoltar></BotaoVoltar>

        </div>
    )
}

export default Cadastro;
