import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate  } from 'react-router-dom';

function EditarRegistro() {

  const { id } = useParams();
  const history = useNavigate();

  const [registro, setRegistro] = useState({
    nome: '',
    idade: '',
    cidade: ''
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:3001/api/buscarId/${id}`)
      .then(response => {
        setRegistro(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError('Houve um problema ao buscar o registro.');
        setLoading(false);
      });
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRegistro({
      ...registro,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:3001/api/usuarios/${id}`, registro)
      .then(response => {
        alert('Registro atualizado com sucesso!');
        history.goBack();
      })
      .catch(error => {
        setError('Houve um problema ao atualizar o registro.');
      });
  };

  if (loading) {
    return <p>Carregando...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div className="editar-registro">
      <h2>Editar Registro</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nome:</label>
          <input type="text" name="nome" value={registro.nome}  onChange={handleChange}  required />
        </div>
        <div>
          <label>Idade:</label>
          <input
            type="number" name="idade" value={registro.idade} onChange={handleChange} required />
        </div>
        <div>
          <label>Cidade:</label>
          <input type="text" name="cidade"  value={registro.cidade} onChange={handleChange}  required />
        </div>
        <button type="submit">Salvar</button>
      </form>
    </div>
  );
}

export default EditarRegistro;
