import React, { useState } from 'react';

const BuscarArquivo = () => {
  const [imageUrl, setImageUrl] = useState(null);
  const [error, setError] = useState(null);
  const [fileId, setFileId] = useState('');

  const buscarArquivo = async (id) => {
    try {
      const response = await fetch(`http://localhost:3001/api/uploadarquivo/${id}`);
      if (!response.ok) {
        throw new Error('Erro ao buscar o arquivo');
      }
      const blob = await response.blob();
      const url = URL.createObjectURL(blob);
      setImageUrl(url);
    } catch (err) {
      console.log("Erro ao buscar arquivo");
      setError(err.message);
    }
  };

  return (
    // <div>
    //   <input
    //     type="text"
    //     placeholder="Informe o ID do arquivo"
    //     onBlur={(e) => fetchImage(e.target.value)}
    //   />
    //   {error && <p>{error}</p>}
    //   {imageUrl && <img src={imageUrl} alt="Arquivo encontrado" />}
    // </div>

      <div className="buscar-arquivo-container">
      <h2>Buscar Arquivo</h2>
      <div className="input-group">
        <input
          type="text"
          placeholder="Informe o ID do arquivo"
          value={fileId}
          onChange={(e) => setFileId(e.target.value)}
        />
        <button onClick={buscarArquivo}>Buscar</button>
      </div>
      {error && <p className="error">{error}</p>}
      {fileUrl && <img src={imageUrl} alt="Arquivo encontrado" className="file-preview" />}
      </div>

  );
};

export default BuscarArquivo;
